mapper.select:
SELECT COUNT(*) FROM sys_user_activity_log

    action_type = 'view_news'
    AND created_at >= #{startDate}
    AND created_at <= #{endDate}

SELECT COUNT(*) FROM sys_user_activity_log

    action_type = 'register_meeting'
    AND created_at >= #{startDate}
    AND created_at <= #{endDate}

SELECT DATE(created_at) AS date, SUM(CASE WHEN action_type = 'view_news' THEN 1 ELSE 0 END) AS views, SUM(CASE WHEN action_type = 'register_meeting' THEN 1 ELSE 0 END) AS registrations FROM sys_user_activity_log
AND created_at >= #{startDate} AND created_at <= #{endDate}
GROUP BY DATE(created_at) ORDER BY date ASC

SELECT t.target_id as id, b.title as title, COUNT(t.id) as views FROM sys_user_activity_log t JOIN biz_news b ON t.target_id = b.id
t.action_type = 'view_news' AND t.created_at >= #{startDate} AND t.created_at <= #{endDate}
GROUP BY t.target_id, b.title ORDER BY views DESC LIMIT 5
